app.post("/create", function (req, res) {
  var cedula = req.body.cedula;
  var nombre = req.body.nombre;
  var apellido = req.body.apellido;
  var correo = req.body.correo;
  db.query('INSERT INTO usuarios(cedula, nombre, apellido, correo) VALUES(?,?,?,?)', [cedula, nombre, apellido, correo], function (err, result) {
    if (err) {
      console.log(err);
    } else {
      res.send("usuario registrado");
    }
  });
});
app.get("/usuarios", function (req, res) {
  var response;
  db.query('SELECT * FROM usuarios', function (err, result) {
    if (err) {
      response = err;
    } else {
      response = res.send(result);
    }
  });
  return response;
});
app["delete"]("/deleteUsuarios/:cedula", function (req, res) {
  var cedula = req.params.cedula;
  db.query('DELETE from usuarios WHERE cedula=?', [cedula], function (err, result) {
    if (err) {
      console.log(err);
    } else {
      res.send("usuario eliminado con exito");
    }
  });
});

//insumos
app.post("/createInsumos", function (req, res) {
  var codigo = req.body.codigo;
  var nombre = req.body.nombre;
  var cantidad = req.body.cantidad;
  var imagen = req.body.imagen;
  var f_ingreso = req.body.f_ingreso;
  var f_vencimiento = req.body.f_vencimiento;
  var costo = req.body.costo;
  db.query('INSERT INTO insumos(codigo, nombre, cantidad, imagen, f_ingreso, f_vencimiento, costo) VALUES(?,?,?,?,?,?,?)', [codigo, nombre, cantidad, imagen, f_ingreso, f_vencimiento, costo], function (err, result) {
    if (err) {
      console.log(err);
    } else {
      res.send("insumo registrado");
    }
  });
});
app.put("/updateInsumos", function (req, res) {
  var codigo = req.body.codigo;
  var nombre = req.body.nombre;
  var cantidad = req.body.cantidad;
  var imagen = req.body.imagen;
  var f_ingreso = req.body.f_ingreso;
  var f_vencimiento = req.body.f_vencimiento;
  var costo = req.body.costo;
  console.log(f_ingreso);
  db.query('UPDATE insumos SET imagen=?, nombre=?, cantidad=?, f_ingreso=?, f_vencimiento=?, costo=? WHERE codigo=?', [imagen, nombre, cantidad, f_ingreso, f_vencimiento, costo, codigo], function (err, result) {
    if (err) {
      console.log(err);
    } else {
      res.send("insumo actualizado");
    }
  });
});
app.get("/insumos", function (req, res) {
  var response;
  db.query('SELECT * FROM insumos', function (err, result) {
    if (err) {
      response = err;
    } else {
      response = res.send(result);
    }
  });
  return response;
});
app.get("/insumo/:codigo", function (req, res) {
  var codigo = req.params.codigo;
  var response;
  db.query('SELECT * FROM insumos WHERE codigo=?', [codigo], function (err, result) {
    if (err) {
      response = err;
    } else {
      response = res.send(result[0]);
    }
  });
