const express = require("express");
const app = express();
const mysql = require("mysql2/promise");
const cors = require("cors")
const multer = require('multer');
const path = require("path");

//Configure Multer Disk Storage
const storage = multer.diskStorage({
    destination: (req, file, cb) => {
        cb(null, "images");
    },
    filename: (req, file, cb) => {
        cb(null, uuidv4() + path.extname(file.originalname));
    },
});

//Filter Multer Files With Mime Type
const fileFilter = (req, file, cb) => {
    if (
        file.mimetype === "image/jpg" ||
        file.mimetype === "image/png" ||
        file.mimetype === "image/jpeg" ||
        file.mimetype === "image/svg"
    ) {
        cb(null, true);
    } else {
        cb(null, false);
        console.log("Invalid File Extension");
    }
};

// Use Body Parser, Express Static, Multer
//   app.use(bodyParser.urlencoded({ extended: false }));
app.use(
    multer({ storage: storage, fileFilter: fileFilter }).fields([
        { name: "songfile", maxCount: 1 },
        { name: "cover", maxCount: 1 },
    ])
);
app.use(express.static(path.join(__dirname, "public")));
app.use("/images", express.static(path.join(__dirname, "images")));
app.use(cors());
app.use(express.json());

const db = mysql.createConnection({
    host: "localhost",
    port: 3306,
    user: "root",
    password: "",
    database: "senacafe"
});

//login

const inicioSesion = async (req, res) => {
    try{
        const { correo, contrasena } = req.body;
        const usuario = await db.query("select * from usuarios where correo = ?", [correo]);

        if (!user.length) {
            res.status(404).json({ error: "Usuario o contraseña incorrectos" });
        } else if (contrasena !== user[0].cedula) {   // Comparar contraseña con la almacenada
            res.status(401).json({ error: "Usuario o contraseña incorrectos" });
        } else {
            // Generar una respuesta con el usuario
            const response = {
                nombre: user[0].nombre,
                correo: user[0].correo,
            };
            // Enviar la respuesta al cliente
            res.send(response);      
        }
    }catch (err) {
        console.log(err);
        res.status(500).json({error: "Error al obtener usuario"});
    }
}; 




//usuarios
app.post("/create", (req, res) => {
    const cedula = req.body.cedula;
    const nombre = req.body.nombre;
    const apellido = req.body.apellido;
