sourcesContent:
import {getConnection} from "./../databases/database";


// pruebas para el funcionamiento del CRUD
const getExamp = async (req, res) => {
    try{
        const connection = await getConnection();
        const result = await connection.query("select * from usuarios");
        console.log(result);
        res.json(result);

    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const addExamp = async (req, res) => {
    try{
        const { cedula, nombre, apellido, correo } = req.body;
        if (cedula === undefined || nombre === undefined || apellido === undefined || correo === undefined) {
            res.status(400).json({message: "Bad request. Please fill all field."});
        }

        const newuser = { cedula, nombre, apellido, correo };
        const connection = await getConnection();
        const result = await connection.query("insert into usuarios set ?", newuser);
        res.json("User added");

    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const addExampId = async (req, res) => {
    try{
        const { cedula } = req.params;
        const connection = await getConnection();
        const result = await connection.query("select * from usuarios where cedula = ?", cedula);
        res.json(result);

    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const deleteExampId = async (req, res) => {
    try{
        const { cedula } = req.params;
        const connection = await getConnection();
        const result = await connection.query("delete from usuarios where cedula = ?", cedula);
        res.json(result);

    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const updateExamp = async (req, res) => {
    try{
        const { cedula } = req.params;
        const {nombre, apellido, correo } = req.body;
        if (cedula === undefined || nombre === undefined || apellido === undefined || correo === undefined) {
            res.status(400).json({message: "Bad request. Please fill all field."});
        }

        const newuser = {nombre, apellido, correo };
        const connection = await getConnection();
        const result = await connection.query("update usuarios set ? where cedula = ?", [newuser, cedula]);
        res.json(result);

    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};
// finalizacion de pruebas al CRUD


// FALTA CORREGIR   Iniciar sesion
const inicioSesion = async (req, res) => {
    try{
        const { cedula,  correo } = req.body;
        const connection = await getConnection();
        const users = await connection.query("select * from usuarios where correo = ?", [correo]);

        if (!users || users.length === 0) {
            res.status(404).json({ error: "Usuario o contraseña incorrectos" });
        } else if (cedula === users.cedula) {   
            // Generar una respuesta con el usuario
            const user = users[0];
            const response = {
                nombre: user.nombre,
                correo: user.correo
            };
            // Enviar la respuesta al cliente
            res.json(response);      
        }
    }catch (err) {
        console.log(err);
        res.status(500).json({error: "Error al obtener usuario"});
    }
}; 

//  usuarios
const agregarUsuario = async (req, res) => {
    try {
        const {nombre, apellido, correo, cedula} = req.body;
        const newUser ={   
            nombre,
            apellido,
            correo,
            cedula
        };
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO usuarios set ?", newUser);
        
        res.json("user add");
    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const mostrarUsuarios = async (req,res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM usuarios");
        res.json(result);
    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

const eliminarUsuario = async (req, res) => {
    try {
        const { cedula } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM usuarios where cedula = ?", cedula);
        res.json(result);
    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};

export const querys = {
    getExamp,
    addExamp,
    addExampId,
    deleteExampId,
    updateExamp,
    inicioSesion,
    agregarUsuario,
    mostrarUsuarios,
    eliminarUsuario,
};

