// Balance diario
const balanceDiario = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT facturas.*, COALESCE(sumTotalIngresos, 0) AS sumTotalIngresos, COALESCE(sumTotalEgresos, 0) AS sumTotalEgresos FROM ( SELECT * FROM facturas WHERE DATE(fecha) = CURDATE() ) AS facturas, (SELECT SUM(total) as sumtotalIngresos FROM facturas WHERE total > 0 AND fecha = CURDATE()) AS totalIngresos, (SELECT SUM(total) as sumTotalEgresos FROM facturas WHERE total < 0 AND fecha = CURDATE()) as totalEgresos;");
        res.json(result);
    } catch (err) {
        res.status(500);
        res.send(err.message);
    }
};


// Balance mensual
const balanceMensual = async (req, res) => {
