        const result = await connection.query("SELECT facturas.*, COALESCE(sumTotalIngresos, 0) AS sumTotalIngresos, COALESCE(sumTotalEgresos, 0) AS sumTotalEgresos FROM ( SELECT * FROM facturas WHERE MONTH(fecha) = MONTH(CURDATE()) AND YEAR(fecha) = YEAR(CURDATE()) ) AS facturas, (SELECT SUM(total) as sumTotalIngresos FROM facturas WHERE total > 0 AND MONTH(fecha) = MONTH(CURRENT_DATE)) AS totalIngresos, (SELECT SUM(total) as sumTotalEgresos FROM facturas WHERE total < 0 AND MONTH(fecha) = MONTH(CURRENT_DATE)) as totalEgresos;");
        res.json(result);
